sci:arg.sci:enabledscript:
SetDisabledDetails(
	 "To enable " . ParameterName() . " do not specify in the Data Source definition or string (UID=xxx)");
	(Parameter('Data Source') not rlike '.;UID=[^;]+(;|$)');

SetDisabledDetails("To enable, set Authentication Method to 'Use Username and Password Parameters' and do not specify in <i>Data Source</i>");
Parameter('Authentication Method') eq 'Use Username and Password Parameters' and
	Parameter('Data Source') not rlike '.;PWD=[^;]+(;|$)';

SetDisabledDetails(
	 "To enable " . ParameterName() . " set Separate Connection to 'True'");
(not Parameter('Separate Connection') is defined) or Parameter('Separate Connection') eq 'True';

SetDisabledDetails(
	 "To enable " . ParameterName() . " set Separate Connection to 'True'");
(not Parameter('Separate Connection') is defined) or Parameter('Separate Connection') eq 'True';

SetDisabledDetails(
	 "To enable " . ParameterName() . " do not specify in the Data Source definition or string (UID=xxx)");
	(Parameter('Data Source') not rlike '.;UID=[^;]+(;|$)');

SetDisabledDetails("To enable, set Authentication Method to 'Use Username and Password Parameters' and do not specify in <i>Data Source</i>");
Parameter('Authentication Method') eq 'Use Username and Password Parameters' and
	Parameter('Data Source') not rlike '.;PWD=[^;]+(;|$)';

#parallel := Parameter('Parallel Processing Options');
#rtcForEachCategory := (Parameter('RunToCompletion') eq 'True - For Each Category');
if (not #parallel) then
	SetDisabledDetails("This parameter is only applicable if 'Parallel Processing Options' is 'True'");
elsif (#rtcForEachCategory) then
	SetDisabledDetails("This parameter is not applicable when 'RunToCompletion' is set to 'True - For Each Category'");
end if;
#parallel and not #rtcForEachCategory;

sci:arg.sci:validscript:
/* Server Processes must have the same number of comma separated entries as the Server parameter and must have a value*/
#processes := parameter();
expand(',', #processes);
#servers := parameter('Parallel Processing Options Server');
expand(',', #servers);

if (numvalues(#processes) == 0) then
	SetInvalidDetails('This parameter is required.');
else
	SetInvalidDetails('The number of server processes (' . numvalues(#processes) . ') must equal the number of servers (' . numvalues(#servers) . ').');
end if;
numvalues(#processes) > 0 and numvalues(#processes) == numvalues(#servers);

sci:arg.sci:value:
/* replace target state placeholder in XML with proper value */

@RequestBody := "";

for #i in 1 .. numvalues(xml)
loop
	if FindSubString(xml[#i],"REPLACE_ME") > 0 then 
		xml[#i] := StrReplace(xml[#i],"REPLACE_ME",GlobalProperty("Target State")); 
	end if;
	
	@RequestBody .= xml[#i];
	
end loop;


RequestBody := @RequestBody;

GlobalProperty("Set_Status") := true;

GlobalProperty("DB_Type_Names") := GlobalProperty("Entity Type Name");
GlobalProperty("DB_State_Values") := GlobalProperty("Entity State");

/* convert to  arrays for processing */
Expand(",",GlobalProperty("DB_Type_Names"));
Expand(",",GlobalProperty("DB_State_Values"));

Types := GlobalProperty("DB_Type_Names");
States := GlobalProperty("DB_State_Values");

